import React, { useState, useEffect } from 'react';

const Projects = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('projects');
    if (element) observer.observe(element);

    return () => observer.disconnect();
  }, []);

  const projects = [
    {
      title: 'React Shopping Website',
      description: 'A React-based e-commerce site with search, sorting, cart, and image upload using Cloudinary and MERN stack.',
      tech: ['React', 'Node.js', 'MongoDB', 'Cloudinary'],
      github: 'https://github.com/visheshpanchal27/Mern',
      live: 'https://mern-dvqh.onrender.com',
      image: 'üõí'
    },
    {
      title: 'LV Shopping Center',
      description: 'Java-based online shopping platform with cart, UI, and payment features.',
      tech: ['Java', 'Swing', 'MySQL', 'Payment Gateway'],
      github: 'https://github.com/visheshpanchal27/LV_Shopping_Center',
      live: null,
      image: 'üè™'
    },
    {
      title: 'Portfolio Website',
      description: 'Modern responsive portfolio built with React and Tailwind CSS featuring dark mode and smooth animations.',
      tech: ['React', 'Tailwind CSS', 'EmailJS', 'CSS Animations'],
      github: '#',
      live: '#',
      image: 'üíº'
    }
  ];

  return (
    <section id="projects" className="py-20 px-4">
      <div className="container mx-auto">
        <h2 className={`text-3xl md:text-4xl font-bold text-primary text-center mb-4 ${isVisible ? 'animate-slide-up' : 'opacity-0'}`}>
          Featured Projects
        </h2>
        <p className={`text-center text-gray-600 dark:text-gray-300 mb-12 max-w-2xl mx-auto ${isVisible ? 'animate-fade-in' : 'opacity-0'}`} style={{ animationDelay: '0.2s' }}>
          Here are some of my recent projects that showcase my skills and experience in web development.
        </p>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {projects.map((project, index) => (
            <div
              key={index}
              className={`bg-white dark:bg-gray-800 rounded-xl shadow-lg overflow-hidden hover:shadow-2xl transition-all duration-300 group hover:scale-105 hover:-translate-y-2 ${isVisible ? 'animate-scale-in' : 'opacity-0'}`}
              style={{ animationDelay: `${index * 0.2}s` }}
            >
              <div className="h-48 bg-gradient-to-br from-primary/20 to-accent/20 flex items-center justify-center text-6xl group-hover:scale-110 transition-transform duration-300">
                {project.image}
              </div>

              <div className="p-6">
                <h3 className="text-xl font-bold text-primary mb-3 group-hover:text-accent transition-colors">
                  {project.title}
                </h3>
                <p className="text-gray-600 dark:text-gray-300 mb-4 text-sm leading-relaxed">
                  {project.description}
                </p>

                <div className="flex flex-wrap gap-2 mb-6">
                  {project.tech.map((tech, techIndex) => (
                    <span
                      key={techIndex}
                      className="px-3 py-1 bg-primary/10 text-primary text-xs rounded-full font-medium hover:bg-primary/20 transition-colors duration-300"
                    >
                      {tech}
                    </span>
                  ))}
                </div>

                <div className="flex gap-3">
                  <a
                    href={project.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex-1 border-2 border-primary text-primary hover:bg-primary hover:text-white px-4 py-2 rounded-lg transition-all duration-300 text-center text-sm font-medium hover:scale-105"
                  >
                    <i className="fab fa-github mr-2"></i>Code
                  </a>
                  {project.live && (
                    <a
                      href={project.live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex-1 bg-primary hover:bg-accent text-white px-4 py-2 rounded-lg transition-all duration-300 text-center text-sm font-medium hover:scale-105"
                    >
                      <i className="fas fa-external-link-alt mr-2"></i>Live
                    </a>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
