import React, { useState, useEffect } from 'react';

const About = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('about');
    if (element) observer.observe(element);

    return () => observer.disconnect();
  }, []);

  const stats = [
    { number: '2+', label: 'Years Learning', icon: 'fas fa-calendar-alt' },
    { number: '10+', label: 'Projects Built', icon: 'fas fa-code' },
    { number: '5+', label: 'Technologies', icon: 'fas fa-tools' },
    { number: '100%', label: 'Dedication', icon: 'fas fa-heart' }
  ];

  return (
    <section id="about" className="py-20 px-4">
      <div className="container mx-auto">
        <h2 className={`text-3xl md:text-4xl font-bold text-primary text-center mb-12 ${isVisible ? 'animate-slide-up' : 'opacity-0'}`}>
          About Me
        </h2>

        <div className="grid lg:grid-cols-2 gap-12 items-center max-w-6xl mx-auto">
          <div className={`space-y-6 ${isVisible ? 'animate-slide-right' : 'opacity-0'}`}>
            <div className="text-lg leading-relaxed space-y-4">
              <p className="text-gray-700 dark:text-gray-300">
                Hi! I'm <span className="text-primary font-semibold">Vishesh Panchal</span>, a passionate diploma student specializing in web development. I love turning ideas into reality through clean, efficient code.
              </p>
              <p className="text-gray-700 dark:text-gray-300">
                My journey in programming started with curiosity and has evolved into a deep passion for creating user-friendly applications. I enjoy solving complex problems and learning new technologies.
              </p>
              <p className="text-gray-700 dark:text-gray-300">
                When I'm not coding, you'll find me exploring new frameworks, contributing to open-source projects, or planning my next big project. My goal is to become a skilled full-stack developer and make a positive impact in the tech world.
              </p>
            </div>

            <div className="flex flex-wrap gap-3 mt-6">
              {['Problem Solver', 'Quick Learner', 'Team Player', 'Creative Thinker'].map((trait, index) => (
                <span 
                  key={index}
                  className={`px-4 py-2 bg-primary/10 text-primary rounded-full text-sm font-medium hover:bg-primary/20 transition-colors duration-300 ${isVisible ? 'animate-fade-in' : 'opacity-0'}`}
                  style={{ animationDelay: `${0.3 + index * 0.1}s` }}
                >
                  {trait}
                </span>
              ))}
            </div>
          </div>

          <div className={`grid grid-cols-2 gap-6 ${isVisible ? 'animate-slide-left' : 'opacity-0'}`}>
            {stats.map((stat, index) => (
              <div
                key={index}
                className={`bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg text-center hover:shadow-2xl hover:scale-105 transition-all duration-300 ${isVisible ? 'animate-scale-in' : 'opacity-0'}`}
                style={{ animationDelay: `${0.4 + index * 0.1}s` }}
              >
                <i className={`${stat.icon} text-3xl text-primary mb-3 block hover:scale-110 transition-transform duration-300`}></i>
                <div className="text-2xl font-bold text-primary mb-1">
                  {stat.number}
                </div>
                <div className="text-gray-600 dark:text-gray-300 text-sm font-medium">
                  {stat.label}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
