import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  const [displayText, setDisplayText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const texts = ['Full Stack Developer', 'React Enthusiast', 'Problem Solver', 'Creative Coder'];

  useEffect(() => {
    let i = 0;
    const currentText = texts[currentIndex];
    const typeWriter = () => {
      if (i < currentText.length) {
        setDisplayText(currentText.slice(0, i + 1));
        i++;
        setTimeout(typeWriter, 100);
      } else {
        setTimeout(() => {
          setDisplayText('');
          i = 0;
          setCurrentIndex((prev) => (prev + 1) % texts.length);
        }, 2000);
      }
    };
    typeWriter();
  }, [currentIndex]);

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-green-600 via-primary to-cyan-500 dark:from-green-800 dark:via-green-600 dark:to-cyan-700"></div>

      <motion.div 
        className="relative z-10 text-center text-white"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <motion.h1 
          className="text-4xl md:text-6xl font-bold mb-4"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          Hi, I'm{' '}
          <motion.span 
            className="bg-white text-gray-900 px-2 rounded inline-block"
            whileHover={{ scale: 1.05, rotate: 2 }}
          >
            Vishesh Panchal
          </motion.span>
        </motion.h1>

        <motion.div 
          className="text-xl md:text-2xl font-light mb-8 h-8"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <span className="text-yellow-300">{displayText}</span>
          <motion.span 
            className="text-white"
            animate={{ opacity: [1, 0] }}
            transition={{ duration: 0.8, repeat: Infinity }}
          >
            |
          </motion.span>
        </motion.div>

        <motion.div 
          className="flex flex-col sm:flex-row gap-4 justify-center"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <motion.a
            href="#projects"
            className="bg-gray-900 hover:bg-primary text-white px-8 py-3 rounded-lg transition-all duration-300"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            View Work
          </motion.a>

          <motion.a
            href="#contact"
            className="border-2 border-white hover:bg-white hover:text-gray-900 text-white px-8 py-3 rounded-lg transition-all duration-300"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Contact Me
          </motion.a>
        </motion.div>

        <motion.div
          className="mt-12"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity }}
        >
          <a href="#about" className="text-white/70 hover:text-white transition-colors">
            <i className="fas fa-chevron-down text-2xl"></i>
          </a>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Hero;
