import React from 'react';

const Projects = () => {
  const projects = [
    {
      title: 'React Shopping Website',
      description: 'A React-based e-commerce site with search, sorting, cart, and image upload using Cloudinary and MERN stack.',
      github: 'https://github.com/visheshpanchal27/Mern',
      live: 'https://mern-dvqh.onrender.com'
    },
    {
      title: 'LV Shopping Center',
      description: 'Java-based online shopping platform with cart, UI, and payment features.',
      github: 'https://github.com/visheshpanchal27/LV_Shopping_Center',
      live: null
    }
  ];

  return (
    <section id="projects" className="py-20 px-4">
      <div className="container mx-auto">
        <h2 className="text-3xl md:text-4xl font-bold text-primary text-center mb-12">Projects</h2>
        <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
          {projects.map((project, index) => (
            <div
              key={index}
              className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg hover:transform hover:-translate-y-2 hover:scale-102 transition-all duration-300 flex flex-col"
            >
              <h3 className="text-xl font-bold text-primary mb-3">{project.title}</h3>
              <p className="text-gray-600 dark:text-gray-300 mb-6 flex-grow">{project.description}</p>
              <div className="flex gap-4">
                <a
                  href={project.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="border-2 border-primary text-primary hover:bg-primary hover:text-white px-4 py-2 rounded-lg transition-all duration-300 hover:scale-105 flex items-center gap-2"
                >
                  <i className="fab fa-github"></i> GitHub
                </a>
                {project.live && (
                  <a
                    href={project.live}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-primary hover:bg-accent text-white px-4 py-2 rounded-lg transition-all duration-300 hover:scale-105 flex items-center gap-2"
                  >
                    <i className="fas fa-external-link-alt"></i> Live Preview
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
