import React from 'react';

const About = () => {
  return (
    <section id="about" className="py-20 px-4">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl md:text-4xl font-bold text-primary mb-8">About Me</h2>
        <div className="max-w-2xl mx-auto">
          <p className="text-lg leading-relaxed">
            I'm a diploma student passionate about web development. I enjoy solving problems and building creative websites. My goal is to become a full-stack developer.
          </p>
        </div>
      </div>
    </section>
  );
};

export default About;
