import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [displayText, setDisplayText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const texts = ['Full Stack Developer', 'React Enthusiast', 'Problem Solver', 'Creative Coder'];

  useEffect(() => {
    let i = 0;
    const currentText = texts[currentIndex];
    const typeWriter = () => {
      if (i < currentText.length) {
        setDisplayText(currentText.slice(0, i + 1));
        i++;
        setTimeout(typeWriter, 100);
      } else {
        setTimeout(() => {
          setDisplayText('');
          i = 0;
          setCurrentIndex((prev) => (prev + 1) % texts.length);
        }, 2000);
      }
    };
    typeWriter();
  }, [currentIndex]);

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-green-600 via-primary to-cyan-500 dark:from-green-800 dark:via-green-600 dark:to-cyan-700"></div>

      <div className="relative z-10 text-center text-white animate-fade-in">
        <h1 className="text-4xl md:text-6xl font-bold mb-4 animate-slide-up">
          Hi, I'm{' '}
          <span className="bg-white text-gray-900 px-2 rounded inline-block hover:scale-105 transition-transform duration-300">
            Vishesh Panchal
          </span>
        </h1>

        <div className="text-xl md:text-2xl font-light mb-8 h-8">
          <span className="text-yellow-300">{displayText}</span>
          <span className="text-white animate-pulse">|</span>
        </div>

        <div className="flex flex-col sm:flex-row gap-4 justify-center animate-slide-up">
          <a
            href="#projects"
            className="bg-gray-900 hover:bg-primary text-white px-8 py-3 rounded-lg transition-all duration-300 hover:scale-105 hover:shadow-lg"
          >
            View Work
          </a>

          <a
            href="#contact"
            className="border-2 border-white hover:bg-white hover:text-gray-900 text-white px-8 py-3 rounded-lg transition-all duration-300 hover:scale-105"
          >
            Contact Me
          </a>
        </div>

        <div className="mt-12 animate-bounce-slow">
          <a href="#about" className="text-white/70 hover:text-white transition-colors">
            <i className="fas fa-chevron-down text-2xl"></i>
          </a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
