import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  const stats = [
    { number: '2+', label: 'Years Learning', icon: 'fas fa-calendar-alt' },
    { number: '10+', label: 'Projects Built', icon: 'fas fa-code' },
    { number: '5+', label: 'Technologies', icon: 'fas fa-tools' },
    { number: '100%', label: 'Dedication', icon: 'fas fa-heart' }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.6 }
    }
  };

  return (
    <section id="about" className="py-20 px-4">
      <div className="container mx-auto">
        <motion.h2 
          className="text-3xl md:text-4xl font-bold text-primary text-center mb-12"
          initial={{ opacity: 0, y: -50 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          About Me
        </motion.h2>

        <div className="grid lg:grid-cols-2 gap-12 items-center max-w-6xl mx-auto">
          <motion.div
            className="space-y-6"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <div className="text-lg leading-relaxed space-y-4">
              <p className="text-gray-700 dark:text-gray-300">
                Hi! I'm <span className="text-primary font-semibold">Vishesh Panchal</span>, a passionate diploma student specializing in web development. I love turning ideas into reality through clean, efficient code.
              </p>
              <p className="text-gray-700 dark:text-gray-300">
                My journey in programming started with curiosity and has evolved into a deep passion for creating user-friendly applications. I enjoy solving complex problems and learning new technologies.
              </p>
              <p className="text-gray-700 dark:text-gray-300">
                When I'm not coding, you'll find me exploring new frameworks, contributing to open-source projects, or planning my next big project. My goal is to become a skilled full-stack developer and make a positive impact in the tech world.
              </p>
            </div>

            <motion.div 
              className="flex flex-wrap gap-3 mt-6"
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              {['Problem Solver', 'Quick Learner', 'Team Player', 'Creative Thinker'].map((trait, index) => (
                <motion.span 
                  key={index}
                  className="px-4 py-2 bg-primary/10 text-primary rounded-full text-sm font-medium hover:bg-primary/20 transition-colors duration-300"
                  variants={itemVariants}
                  whileHover={{ scale: 1.05 }}
                >
                  {trait}
                </motion.span>
              ))}
            </motion.div>
          </motion.div>

          <motion.div
            className="grid grid-cols-2 gap-6"
            variants={containerVariants}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow-lg text-center hover:shadow-2xl transition-all duration-300"
                variants={itemVariants}
                whileHover={{ 
                  scale: 1.05,
                  rotateY: 5
                }}
              >
                <motion.i 
                  className={`${stat.icon} text-3xl text-primary mb-3 block`}
                  whileHover={{ scale: 1.2, rotate: 360 }}
                  transition={{ duration: 0.6 }}
                ></motion.i>
                <motion.div 
                  className="text-2xl font-bold text-primary mb-1"
                  initial={{ scale: 0 }}
                  whileInView={{ scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.5, delay: 0.6 + index * 0.1 }}
                >
                  {stat.number}
                </motion.div>
                <div className="text-gray-600 dark:text-gray-300 text-sm font-medium">
                  {stat.label}
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default About;
