import React, { useState, useEffect } from 'react';

const Hero = () => {
  const [displayText, setDisplayText] = useState('');
  const fullText = 'Full Stack Developer';

  useEffect(() => {
    let i = 0;
    const typeWriter = () => {
      if (i < fullText.length) {
        setDisplayText(fullText.slice(0, i + 1));
        i++;
        setTimeout(typeWriter, 100);
      } else {
        setTimeout(() => {
          setDisplayText('');
          i = 0;
          typeWriter();
        }, 2000);
      }
    };
    typeWriter();
  }, []);

  return (
    <section id="home" className="min-h-screen flex items-center justify-center relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-br from-green-600 via-primary to-cyan-500 dark:from-green-800 dark:via-green-600 dark:to-cyan-700"></div>
      <div className="absolute inset-0 bg-black/20"></div>

      <div className="relative z-10 text-center text-white animate-fade-in">
        <h1 className="text-4xl md:text-6xl font-bold mb-4">
          Hi, I'm <span className="bg-white text-gray-900 px-2 rounded">Vishesh Panchal</span>
        </h1>
        <p className="text-xl md:text-2xl font-light mb-8 h-8">
          {displayText}
          <span className="animate-pulse">|</span>
        </p>
        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <a
            href="#projects"
            className="bg-gray-900 hover:bg-primary text-white px-8 py-3 rounded-lg transition-all duration-300 hover:scale-105"
          >
            View Work
          </a>
          <a
            href="#contact"
            className="border-2 border-white hover:bg-white hover:text-gray-900 text-white px-8 py-3 rounded-lg transition-all duration-300 hover:scale-105"
          >
            Contact Me
          </a>
        </div>
      </div>
    </section>
  );
};

export default Hero;
