import React, { useState, useEffect } from 'react';

const Skills = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('skills');
    if (element) observer.observe(element);

    return () => observer.disconnect();
  }, []);

  const skills = [
    { icon: 'fab fa-html5', name: 'HTML5', level: 90, color: 'text-orange-500' },
    { icon: 'fab fa-css3-alt', name: 'CSS3', level: 85, color: 'text-blue-500' },
    { icon: 'fab fa-js', name: 'JavaScript', level: 80, color: 'text-yellow-500' },
    { icon: 'fab fa-react', name: 'React', level: 75, color: 'text-cyan-500' },
    { icon: 'fas fa-coffee', name: 'Java', level: 70, color: 'text-red-600' },
    { icon: 'fab fa-python', name: 'Python', level: 65, color: 'text-green-500' }
  ];

  return (
    <section id="skills" className="py-20 px-4 bg-gray-50 dark:bg-gray-800">
      <div className="container mx-auto">
        <h2 className={`text-3xl md:text-4xl font-bold text-primary text-center mb-12 ${isVisible ? 'animate-slide-up' : 'opacity-0'}`}>
          Skills & Technologies
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {skills.map((skill, index) => (
            <div
              key={index}
              className={`bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300 group relative overflow-hidden hover:scale-105 ${isVisible ? 'animate-scale-in' : 'opacity-0'}`}
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <div className="text-center mb-4">
                <i className={`${skill.icon} text-5xl ${skill.color} mb-4 block group-hover:scale-110 transition-transform duration-300`}></i>
                <h3 className="font-bold text-lg">{skill.name}</h3>
              </div>

              <div className="w-full bg-gray-200 dark:bg-gray-600 rounded-full h-3 mb-2">
                <div
                  className={`bg-gradient-to-r from-primary to-accent h-3 rounded-full transition-all duration-1500 ${isVisible ? '' : 'w-0'}`}
                  style={{ 
                    width: isVisible ? `${skill.level}%` : '0%',
                    transitionDelay: `${index * 0.1}s`
                  }}
                ></div>
              </div>
              <div className="text-right text-sm text-gray-600 dark:text-gray-300">
                {skill.level}%
              </div>

              <div className="absolute inset-0 bg-gradient-to-r from-primary/10 to-accent/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
