import React from 'react';

const Skills = () => {
  const skills = [
    { icon: 'fab fa-html5', name: 'HTML5' },
    { icon: 'fab fa-css3-alt', name: 'CSS3' },
    { icon: 'fab fa-js', name: 'JavaScript' },
    { icon: 'fas fa-coffee', name: 'Java' },
    { icon: 'fab fa-python', name: 'Python' }
  ];

  return (
    <section id="skills" className="py-20 px-4 bg-gray-50 dark:bg-gray-800">
      <div className="container mx-auto">
        <h2 className="text-3xl md:text-4xl font-bold text-primary text-center mb-12">Skills</h2>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6">
          {skills.map((skill, index) => (
            <div
              key={index}
              className="bg-white dark:bg-gray-700 p-6 rounded-lg shadow-lg text-center hover:transform hover:-translate-y-2 hover:scale-105 transition-all duration-300 group"
            >
              <i className={`${skill.icon} text-4xl text-primary mb-4 group-hover:scale-110 transition-transform duration-300`}></i>
              <h3 className="font-semibold">{skill.name}</h3>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
