import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Skills = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const skills = [
    { icon: 'fab fa-html5', name: 'HTML5', level: 90, color: 'text-orange-500' },
    { icon: 'fab fa-css3-alt', name: 'CSS3', level: 85, color: 'text-blue-500' },
    { icon: 'fab fa-js', name: 'JavaScript', level: 80, color: 'text-yellow-500' },
    { icon: 'fab fa-react', name: 'React', level: 75, color: 'text-cyan-500' },
    { icon: 'fas fa-coffee', name: 'Java', level: 70, color: 'text-red-600' },
    { icon: 'fab fa-python', name: 'Python', level: 65, color: 'text-green-500' }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5
      }
    }
  };

  return (
    <section id="skills" className="py-20 px-4 bg-gray-50 dark:bg-gray-800">
      <div className="container mx-auto">
        <motion.h2 
          className="text-3xl md:text-4xl font-bold text-primary text-center mb-12"
          initial={{ opacity: 0, y: -50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.6 }}
        >
          Skills & Technologies
        </motion.h2>

        <motion.div 
          ref={ref}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto"
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              className="bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300 group relative overflow-hidden"
              variants={itemVariants}
              whileHover={{ 
                scale: 1.05,
                rotateY: 5,
                rotateX: 5
              }}
              style={{
                transformStyle: 'preserve-3d'
              }}
            >
              <div className="text-center mb-4">
                <motion.i 
                  className={`${skill.icon} text-5xl ${skill.color} mb-4 block`}
                  whileHover={{ 
                    scale: 1.2,
                    rotate: 360
                  }}
                  transition={{ duration: 0.6 }}
                ></motion.i>
                <h3 className="font-bold text-lg">{skill.name}</h3>
              </div>

              <div className="w-full bg-gray-200 dark:bg-gray-600 rounded-full h-3 mb-2">
                <motion.div
                  className="bg-gradient-to-r from-primary to-accent h-3 rounded-full"
                  initial={{ width: 0 }}
                  animate={inView ? { width: `${skill.level}%` } : { width: 0 }}
                  transition={{ duration: 1.5, delay: index * 0.1 }}
                ></motion.div>
              </div>
              <div className="text-right text-sm text-gray-600 dark:text-gray-300">
                {skill.level}%
              </div>

              {/* Hover effect overlay */}
              <motion.div
                className="absolute inset-0 bg-gradient-to-r from-primary/10 to-accent/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                initial={{ scale: 0 }}
                whileHover={{ scale: 1 }}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
