import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Projects = () => {
  const [filter, setFilter] = useState('all');
  const [hoveredProject, setHoveredProject] = useState(null);

  const projects = [
    {
      id: 1,
      title: 'E-Commerce Platform',
      description: 'Full-stack MERN application with advanced features like real-time inventory, payment integration, and admin dashboard.',
      longDescription: 'A comprehensive e-commerce solution built with React, Node.js, MongoDB, and integrated with Stripe for payments. Features include user authentication, product management, shopping cart, order tracking, and responsive design.',
      tech: ['React', 'Node.js', 'MongoDB', 'Express', 'Stripe', 'JWT'],
      category: 'fullstack',
      github: 'https://github.com/visheshpanchal27/Mern',
      live: 'https://mern-dvqh.onrender.com',
      image: 'ðŸ›’',
      gradient: 'from-blue-500 to-purple-600',
      features: ['User Authentication', 'Payment Integration', 'Admin Dashboard', 'Real-time Updates']
    },
    {
      id: 2,
      title: 'Task Management App',
      description: 'Modern task management application with drag-and-drop functionality, team collaboration, and progress tracking.',
      longDescription: 'A productivity app inspired by Trello and Asana, featuring drag-and-drop task management, team collaboration tools, file attachments, and detailed analytics dashboard.',
      tech: ['React', 'TypeScript', 'Firebase', 'Material-UI', 'Framer Motion'],
      category: 'frontend',
      github: '#',
      live: '#',
      image: 'ðŸ“‹',
      gradient: 'from-green-500 to-teal-600',
      features: ['Drag & Drop', 'Team Collaboration', 'File Uploads', 'Analytics']
    },
    {
      id: 3,
      title: 'Weather Dashboard',
      description: 'Beautiful weather application with location-based forecasts, interactive maps, and weather alerts.',
      longDescription: 'A comprehensive weather application featuring current conditions, 7-day forecasts, interactive weather maps, severe weather alerts, and location-based recommendations.',
      tech: ['React', 'OpenWeather API', 'Mapbox', 'Chart.js', 'PWA'],
      category: 'frontend',
      github: '#',
      live: '#',
      image: 'ðŸŒ¤ï¸',
      gradient: 'from-orange-500 to-red-600',
      features: ['Live Weather Data', 'Interactive Maps', 'Weather Alerts', 'PWA Support']
    },
    {
      id: 4,
      title: 'Social Media API',
      description: 'RESTful API for social media platform with user management, posts, comments, and real-time notifications.',
      longDescription: 'A robust backend API built with Node.js and Express, featuring user authentication, post management, real-time chat, file uploads, and comprehensive admin controls.',
      tech: ['Node.js', 'Express', 'MongoDB', 'Socket.io', 'Cloudinary', 'JWT'],
      category: 'backend',
      github: '#',
      live: '#',
      image: 'ðŸ”—',
      gradient: 'from-purple-500 to-pink-600',
      features: ['RESTful API', 'Real-time Chat', 'File Uploads', 'Admin Controls']
    },
    {
      id: 5,
      title: 'Portfolio Website',
      description: 'Modern, responsive portfolio website with advanced animations, dark mode, and contact integration.',
      longDescription: 'A cutting-edge portfolio website showcasing modern web development practices with Framer Motion animations, Tailwind CSS styling, and EmailJS integration.',
      tech: ['React', 'Tailwind CSS', 'Framer Motion', 'EmailJS'],
      category: 'frontend',
      github: '#',
      live: '#',
      image: 'ðŸ’¼',
      gradient: 'from-indigo-500 to-purple-600',
      features: ['Modern Design', 'Smooth Animations', 'Dark Mode', 'Contact Form']
    },
    {
      id: 6,
      title: 'Crypto Tracker',
      description: 'Real-time cryptocurrency tracking application with portfolio management and price alerts.',
      longDescription: 'A comprehensive crypto tracking app with real-time price updates, portfolio management, price alerts, market analysis, and detailed coin information.',
      tech: ['React', 'Redux', 'CoinGecko API', 'Chart.js', 'LocalStorage'],
      category: 'frontend',
      github: '#',
      live: '#',
      image: 'â‚¿',
      gradient: 'from-yellow-500 to-orange-600',
      features: ['Real-time Prices', 'Portfolio Tracking', 'Price Alerts', 'Market Analysis']
    }
  ];

  const categories = [
    { id: 'all', name: 'All Projects', icon: 'fas fa-th' },
    { id: 'frontend', name: 'Frontend', icon: 'fas fa-laptop-code' },
    { id: 'backend', name: 'Backend', icon: 'fas fa-server' },
    { id: 'fullstack', name: 'Full Stack', icon: 'fas fa-layer-group' }
  ];

  const filteredProjects = filter === 'all' ? projects : projects.filter(project => project.category === filter);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.2 }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.6 } }
  };

  return (
    <section id="projects" className="py-20 px-4 bg-gradient-to-br from-gray-50 via-white to-blue-50 dark:from-gray-900 dark:via-gray-800 dark:to-blue-900">
      <div className="container mx-auto max-w-7xl">
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: -50 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <h2 className="text-4xl md:text-6xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-4">
            Featured Projects
          </h2>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Showcasing innovative solutions and cutting-edge web applications
          </p>
          <div className="w-24 h-1 bg-gradient-to-r from-blue-600 to-purple-600 mx-auto rounded-full mt-6"></div>
        </motion.div>

        {/* Filter Tabs */}
        <motion.div
          className="flex flex-wrap justify-center gap-4 mb-12"
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          {categories.map((category) => (
            <motion.button
              key={category.id}
              onClick={() => setFilter(category.id)}
              className={`px-6 py-3 rounded-full font-semibold transition-all duration-300 ${
                filter === category.id
                  ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
                  : 'bg-white dark:bg-gray-800 text-gray-600 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
              }`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <i className={`${category.icon} mr-2`}></i>
              {category.name}
            </motion.button>
          ))}
        </motion.div>

        {/* Projects Grid */}
        <motion.div
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-8"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          key={filter}
        >
          {filteredProjects.map((project, index) => (
            <motion.div
              key={project.id}
              className="group relative"
              variants={itemVariants}
              onHoverStart={() => setHoveredProject(project.id)}
              onHoverEnd={() => setHoveredProject(null)}
              whileHover={{ y: -10 }}
            >
              <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl hover:shadow-2xl transition-all duration-500 overflow-hidden border border-gray-100 dark:border-gray-700 relative">
                {/* Project Image/Icon */}
                <div className={`h-48 bg-gradient-to-br ${project.gradient} flex items-center justify-center text-6xl relative overflow-hidden`}>
                  <motion.div
                    className="text-white"
                    animate={hoveredProject === project.id ? { scale: 1.2, rotate: 10 } : { scale: 1, rotate: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    {project.image}
                  </motion.div>

                  {/* Overlay */}
                  <motion.div
                    className="absolute inset-0 bg-black/20 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                    initial={{ scale: 0 }}
                    whileHover={{ scale: 1 }}
                  >
                    <div className="flex gap-4">
                      <motion.a
                        href={project.github}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="w-12 h-12 bg-white/20 backdrop-blur-sm rounded-full flex items-center justify-center text-white hover:bg-white/30 transition-colors"
                        whileHover={{ scale: 1.1 }}
                        whileTap={{ scale: 0.9 }}
                      >
                        <i className="fab fa-github text-xl"></i>
                      </motion.a>
                      {project.live && (
                        <motion.a
                          href={project.live}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="w-12 h-12 bg-white/20 backdrop-blur-sm rounded-full flex items-center justify-center text-white hover:bg-white/30 transition-colors"
                          whileHover={{ scale: 1.1 }}
                          whileTap={{ scale: 0.9 }}
                        >
                          <i className="fas fa-external-link-alt text-xl"></i>
                        </motion.a>
                      )}
                    </div>
                  </motion.div>
                </div>

                <div className="p-6">
                  <h3 className="text-xl font-bold text-gray-800 dark:text-white mb-3 group-hover:text-purple-600 dark:group-hover:text-purple-400 transition-colors">
                    {project.title}
                  </h3>

                  <p className="text-gray-600 dark:text-gray-300 mb-4 text-sm leading-relaxed">
                    {hoveredProject === project.id ? project.longDescription : project.description}
                  </p>

                  {/* Features */}
                  <motion.div
                    className="mb-4 space-y-2"
                    initial={{ height: 0, opacity: 0 }}
                    animate={hoveredProject === project.id ? { height: 'auto', opacity: 1 } : { height: 0, opacity: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    {project.features.map((feature, idx) => (
                      <div key={idx} className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-300">
                        <i className="fas fa-check text-green-500 text-xs"></i>
                        {feature}
                      </div>
                    ))}
                  </motion.div>

                  {/* Tech Stack */}
                  <div className="flex flex-wrap gap-2 mb-6">
                    {project.tech.map((tech, techIndex) => (
                      <motion.span
                        key={techIndex}
                        className={`px-3 py-1 bg-gradient-to-r ${project.gradient} text-white text-xs rounded-full font-medium shadow-sm`}
                        whileHover={{ scale: 1.05 }}
                      >
                        {tech}
                      </motion.span>
                    ))}
                  </div>

                  {/* Action Buttons */}
                  <div className="flex gap-3">
                    <motion.a
                      href={project.github}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex-1 border-2 border-purple-600 text-purple-600 hover:bg-purple-600 hover:text-white px-4 py-2 rounded-lg transition-all duration-300 text-center text-sm font-medium"
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <i className="fab fa-github mr-2"></i>Code
                    </motion.a>
                    {project.live && (
                      <motion.a
                        href={project.live}
                        target="_blank"
                        rel="noopener noreferrer"
                        className={`flex-1 bg-gradient-to-r ${project.gradient} text-white px-4 py-2 rounded-lg transition-all duration-300 text-center text-sm font-medium hover:shadow-lg`}
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <i className="fas fa-external-link-alt mr-2"></i>Live Demo
                      </motion.a>
                    )}
                  </div>
                </div>

                {/* Hover Border Effect */}
                <motion.div
                  className={`absolute inset-0 border-2 border-transparent group-hover:border-purple-300 dark:group-hover:border-purple-600 rounded-2xl transition-all duration-300 opacity-0 group-hover:opacity-100`}
                  initial={{ scale: 0.95 }}
                  whileHover={{ scale: 1 }}
                />
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Call to Action */}
        <motion.div
          className="text-center mt-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <p className="text-lg text-gray-600 dark:text-gray-300 mb-6">
            Interested in working together on your next project?
          </p>
          <motion.a
            href="#contact"
            className="inline-flex items-center gap-3 px-8 py-4 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-semibold rounded-full shadow-xl hover:shadow-2xl transition-all duration-300"
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
          >
            <i className="fas fa-rocket"></i>
            Let's Build Something Amazing
          </motion.a>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
