import React, { useState, useEffect } from 'react';

const Header = ({ darkMode, toggleDarkMode }) => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    { href: '#home', label: 'Home', icon: 'fas fa-home' },
    { href: '#about', label: 'About', icon: 'fas fa-user' },
    { href: '#skills', label: 'Skills', icon: 'fas fa-code' },
    { href: '#projects', label: 'Projects', icon: 'fas fa-folder-open' },
    { href: '#contact', label: 'Contact', icon: 'fas fa-envelope' }
  ];

  const handleNavClick = () => {
    setMobileMenuOpen(false);
  };

  return (
    <header className={`fixed w-full top-0 z-50 transition-all duration-300 ${
      scrolled ? 'bg-white/95 dark:bg-gray-900/95 backdrop-blur-md shadow-lg' : 'bg-transparent'
    }`}>
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <div className="text-xl font-bold bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent hover:scale-105 transition-transform duration-300">
          Vishesh.dev
        </div>

        <nav className="hidden md:flex space-x-8">
          {navItems.map((item, index) => (
            <a
              key={item.href}
              href={item.href}
              className="relative hover:text-primary transition-colors duration-300 group flex items-center gap-2 animate-slide-down"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <i className={`${item.icon} text-sm`}></i>
              {item.label}
              <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-primary transition-all duration-300 group-hover:w-full"></span>
            </a>
          ))}
        </nav>

        <div className="flex items-center gap-4">
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition-all duration-300 hover:scale-110"
            aria-label="Toggle Theme"
          >
            <i className={`fas ${darkMode ? 'fa-sun' : 'fa-moon'} transition-transform duration-300 ${darkMode ? 'rotate-180' : ''}`}></i>
          </button>

          <button
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            className="md:hidden p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition-all duration-300 hover:scale-110"
          >
            <i className={`fas ${mobileMenuOpen ? 'fa-times' : 'fa-bars'} transition-transform duration-300 ${mobileMenuOpen ? 'rotate-180' : ''}`}></i>
          </button>
        </div>
      </div>

      {mobileMenuOpen && (
        <div className="md:hidden bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-700 animate-slide-down">
          <nav className="container mx-auto px-4 py-4 space-y-4">
            {navItems.map((item, index) => (
              <a
                key={item.href}
                href={item.href}
                onClick={handleNavClick}
                className="flex items-center gap-3 p-3 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors duration-300 animate-slide-right"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <i className={`${item.icon} text-primary`}></i>
                {item.label}
              </a>
            ))}
          </nav>
        </div>
      )}
    </header>
  );
};

export default Header;
